/*-- -------------------------- -->
<---      Simple Centered CTA    -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-cta-simple {
    padding: 4rem 1rem;
    background-color: #f8fafc;
  }
  #mattails-cta-simple .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
  }
  #mattails-cta-simple .mattails-content {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto;
  }
  #mattails-cta-simple .mattails-title {
    font-size: clamp(1.75rem, 3vw, 2.5rem);
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-cta-simple .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 2rem;
  }
  #mattails-cta-simple .mattails-button-group {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    justify-content: center;
    max-width: 25rem;
    margin: 0 auto;
  }
  #mattails-cta-simple .mattails-button-primary {
    display: inline-block;
    padding: 0.875rem 2rem;
    background-color: #2563eb;
    color: white;
    font-weight: 600;
    text-decoration: none;
    border-radius: 0.25rem;
    transition: background-color 0.3s, transform 0.3s;
  }
  #mattails-cta-simple .mattails-button-primary:hover {
    background-color: #1d4ed8;
    transform: translateY(-2px);
  }
  #mattails-cta-simple .mattails-button-secondary {
    display: inline-block;
    padding: 0.875rem 2rem;
    background-color: transparent;
    color: #2563eb;
    font-weight: 600;
    text-decoration: none;
    border: 1px solid #2563eb;
    border-radius: 0.25rem;
    transition: background-color 0.3s, color 0.3s;
  }
  #mattails-cta-simple .mattails-button-secondary:hover {
    background-color: rgba(37, 99, 235, 0.1);
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-simple {
    padding: 5rem 1rem;
  }
  #mattails-cta-simple .mattails-button-group {
    flex-direction: row;
    justify-content: center;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-simple {
    padding: 6rem 1rem;
  }

  /* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-dark-stats {
    padding: 5rem 1rem;
  }
  #mattails-cta-dark-stats .mattails-stats-row {
    grid-template-columns: repeat(4, 1fr);
  }
  #mattails-cta-dark-stats .mattails-cta-box {
    flex-direction: row;
    justify-content: space-between;
    text-align: left;
    padding: 3rem;
  }
  #mattails-cta-dark-stats .mattails-cta-action {
    flex-direction: row;
    width: auto;
  }
  #mattails-cta-dark-stats .mattails-cta-button {
    width: auto;
    white-space: nowrap;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-dark-stats {
    padding: 6rem 1rem;
  }
}

/*-- -------------------------- -->
<---    Gradient Background CTA  -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-cta-gradient {
    padding: 4rem 1rem;
    background: linear-gradient(135deg, #4f46e5, #7c3aed);
    position: relative;
    overflow: hidden;
  }
  #mattails-cta-gradient .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    position: relative;
    z-index: 2;
  }
  #mattails-cta-gradient .mattails-content {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto;
  }
  #mattails-cta-gradient .mattails-badge {
    display: inline-block;
    background: rgba(255, 255, 255, 0.2);
    backdrop-filter: blur(8px);
    padding: 0.5rem 1rem;
    border-radius: 2rem;
    font-size: 0.875rem;
    font-weight: 600;
    color: white;
    margin-bottom: 1.5rem;
  }
  #mattails-cta-gradient .mattails-title {
    font-size: clamp(1.75rem, 3vw, 2.5rem);
    font-weight: 800;
    color: white;
    margin: 0 0 1rem;
  }
  #mattails-cta-gradient .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.9);
    margin: 0 0 2rem;
  }
  #mattails-cta-gradient .mattails-cta-action {
    margin-bottom: 1.5rem;
  }
  #mattails-cta-gradient .mattails-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 1rem 2rem;
    background-color: white;
    color: #7c3aed;
    font-weight: 600;
    text-decoration: none;
    border-radius: 0.25rem;
    transition: transform 0.3s, box-shadow 0.3s;
  }
  #mattails-cta-gradient .mattails-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
  #mattails-cta-gradient .mattails-note {
    font-size: 0.875rem;
    color: rgba(255, 255, 255, 0.7);
    margin: 0;
  }

  #mattails-cta-gradient::before {
    content: '';
    position: absolute;
    top: -50%;
    right: -50%;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    z-index: 1;
  }

  #mattails-cta-gradient::after {
    content: '';
    position: absolute;
    bottom: -50%;
    left: -50%;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    z-index: 1;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-gradient {
    padding: 5rem 1rem;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-gradient {
    padding: 6rem 1rem;
  }
}

/*-- -------------------------- -->
<---    Two Column CTA With Image -->
<--- -------------------------- -*/

/* Mobile - 360px */
