    flex-direction: column;
    gap: 1rem;
  }
  #mattails-cta-two-column .mattails-feature {
    display: flex;
    align-items: center;
    gap: 0.75rem;
  }
  #mattails-cta-two-column .mattails-check {
    color: #10b981;
    flex-shrink: 0;
  }
  #mattails-cta-two-column .mattails-cta {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1rem;
  }
