  #mattails-cta-two-column .mattails-no-credit-card {
    font-size: 0.875rem;
    color: #64748b;
    margin: 0;
  }
  #mattails-cta-two-column .mattails-image-side {
    position: relative;
    width: 100%;
    height: 20rem;
  }
  #mattails-cta-two-column .mattails-image-container {
    position: relative;
    width: 100%;
    height: 100%;
  }
  #mattails-cta-two-column .mattails-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 0.5rem;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
  #mattails-cta-two-column .mattails-accent {
    position: absolute;
    width: 80%;
    height: 80%;
    background-color: #bae6fd;
    border-radius: 0.5rem;
    bottom: -1rem;
    right: -1rem;
    z-index: -1;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-two-column {
    padding: 5rem 1rem;
  }
  #mattails-cta-two-column .mattails-container {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    gap: 4rem;
  }
  #mattails-cta-two-column .mattails-content-side {
    width: 50%;
  }
  #mattails-cta-two-column .mattails-image-side {
    width: 45%;
    height: 25rem;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-two-column {
    padding: 6rem 1rem;
  }
  #mattails-cta-two-column .mattails-content-side {
    width: 45%;
  }
  #mattails-cta-two-column .mattails-image-side {
    width: 50%;
    height: 30rem;
  }
}

/*-- -------------------------- -->
<---  Newsletter Subscription CTA -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-cta-newsletter {
    padding: 4rem 1rem;
    background-color: #f0f9ff;
  }
  #mattails-cta-newsletter .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
  }
  #mattails-cta-newsletter .mattails-content {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto;
  }
  #mattails-cta-newsletter .mattails-icon {
    color: #0ea5e9;
    margin: 0 auto;
  }
  #mattails-cta-newsletter .mattails-title {
    font-size: clamp(1.75rem, 3vw, 2.5rem);
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-cta-newsletter .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 2rem;
  }
  #mattails-cta-newsletter .mattails-form {
    margin-bottom: 1.5rem;
  }
  #mattails-cta-newsletter .mattails-input-group {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin-bottom: 1.5rem;
  }
  #mattails-cta-newsletter .mattails-input {
    padding: 1rem;
    border: 1px solid #cbd5e1;
    border-radius: 0.25rem;
    font-size: 1rem;
    width: 100%;
  }
  #mattails-cta-newsletter .mattails-submit {
    padding: 1rem;
    background-color: #0ea5e9;
    color: white;
    font-weight: 600;
    border: none;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.3s;
  }
  #mattails-cta-newsletter .mattails-submit:hover {
    background-color: #0284c7;
  }
  #mattails-cta-newsletter .mattails-checkbox-container {
    display: flex;
    align-items: flex-start;
    gap: 0.5rem;
    text-align: left;
    cursor: pointer;
  }
  #mattails-cta-newsletter .mattails-checkbox-container input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
    height: 0;
    width: 0;
  }
  #mattails-cta-newsletter .mattails-checkmark {
    position: relative;
    display: inline-block;
    min-width: 1.25rem;
    height: 1.25rem;
    background-color: white;
    border: 1px solid #cbd5e1;
    border-radius: 0.25rem;
    margin-top: 0.125rem;
  }
  #mattails-cta-newsletter .mattails-checkbox-container input:checked ~ .mattails-checkmark:after {
    content: '';
    position: absolute;
    display: block;
    left: 0.4rem;
    top: 0.2rem;
    width: 0.25rem;
    height: 0.5rem;
    border: solid white;
    border-width: 0 2px 2px 0;
    transform: rotate(45deg);
  }
  #mattails-cta-newsletter .mattails-checkbox-container input:checked ~ .mattails-checkmark {
    background-color: #0ea5e9;
    border-color: #0ea5e9;
  }
  #mattails-cta-newsletter .mattails-checkbox-text {
    font-size: 0.875rem;
    color: #64748b;
  }
  #mattails-cta-newsletter .mattails-privacy {
    font-size: 0.875rem;
    color: #94a3b8;
    margin: 0;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-newsletter {
    padding: 5rem 1rem;
  }
  #mattails-cta-newsletter .mattails-input-group {
    flex-direction: row;
  }
  #mattails-cta-newsletter .mattails-input {
    border-radius: 0.25rem 0 0 0.25rem;
  }
  #mattails-cta-newsletter .mattails-submit {
    border-radius: 0 0.25rem 0.25rem 0;
    padding: 0 2rem;
    white-space: nowrap;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-newsletter {
    padding: 6rem 1rem;
  }
}

/*-- -------------------------- -->
<---      3D Card Hover CTA        -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-cta-3d-card {
    padding: 4rem 1rem;
    background-color: #f8fafc;
  }
  #mattails-cta-3d-card .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    display: flex;
    justify-content: center;
    perspective: 1000px;
  }
  #mattails-cta-3d-card .mattails-card {
    width: 100%;
    max-width: 30rem;
    transform-style: preserve-3d;
    transition: transform 0.3s ease;
  }
  #mattails-cta-3d-card .mattails-card:hover {
    transform: rotateX(5deg) rotateY(5deg);
  }
  #mattails-cta-3d-card .mattails-card-content {
    background: linear-gradient(135deg, #1e293b, #0f172a);
    padding: 3rem 2rem;
    border-radius: 1rem;
    color: white;
    position: relative;
    overflow: hidden;
    text-align: center;
    box-shadow: 0 30px 50px rgba(0, 0, 0, 0.2);
    transform-style: preserve-3d;
  }
  #mattails-cta-3d-card .mattails-glow {
    position: absolute;
    width: 150%;
    height: 150%;
    top: -25%;
    left: -25%;
    background: radial-gradient(circle, rgba(59, 130, 246, 0.3) 0%, transparent 70%);
    opacity: 0;
    transition: opacity 0.5s ease;
    pointer-events: none;
  }
  #mattails-cta-3d-card .mattails-card:hover .mattails-glow {
    opacity: 1;
  }
  #mattails-cta-3d-card .mattails-icon-container {
    width: 4rem;
    height: 4rem;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 2rem;
    color: #3b82f6;
    position: relative;
    transform: translateZ(20px);
  }
  #mattails-cta-3d-card .mattails-title {
    font-size: 1.75rem;
    font-weight: 700;
    margin: 0 0 1rem;
    position: relative;
    transform: translateZ(15px);
  }
  #mattails-cta-3d-card .mattails-description {
    font-size: 1rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.8);
    margin: 0 0 2rem;
    position: relative;
    transform: translateZ(10px);
  }
  #mattails-cta-3d-card .mattails-action {
    position: relative;
    transform: translateZ(25px);
  }
  #mattails-cta-3d-card .mattails-button {
    display: inline-block;
    padding: 1rem 2.5rem;
    background: linear-gradient(90deg, #3b82f6, #60a5fa);
    color: white;
    font-weight: 600;
    text-decoration: none;
    border-radius: 0.5rem;
    transition: transform 0.3s, box-shadow 0.3s;
  }
  #mattails-cta-3d-card .mattails-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 25px rgba(59, 130, 246, 0.4);
  }
  #mattails-cta-3d-card .mattails-shape {
    position: absolute;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    pointer-events: none;
  }
  #mattails-cta-3d-card .mattails-shape-1 {
    width: 8rem;
    height: 8rem;
    top: -2rem;
    right: -2rem;
    transform: translateZ(5px);
  }
  #mattails-cta-3d-card .mattails-shape-2 {
    width: 6rem;
    height: 6rem;
    bottom: 2rem;
    left: -2rem;
    transform: translateZ(5px);
  }
  #mattails-cta-3d-card .mattails-shape-3 {
    width: 10rem;
    height: 10rem;
    bottom: -5rem;
    right: 2rem;
    transform: translateZ(5px);
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-cta-3d-card {
    padding: 5rem 1rem;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-cta-3d-card {
    padding: 6rem 1rem;
  }
}

