/*-- -------------------------- -->
<---      Countdown Timer CTA      -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-cta-countdown {
    padding: 4rem 1rem;
    background: linear-gradient(135deg, #1e293b, #0f172a);
    color: white;
  }
  #mattails-cta-countdown .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
  }
  #mattails-cta-countdown .mattails-content {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto;
  }
  #mattails-cta-countdown .mattails-title {
    font-size: clamp(1.75rem, 3vw, 2.5rem);
    font-weight: 700;
    margin: 0 0 1rem;
  }
  #mattails-cta-countdown .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.8);
    margin: 0 0 2.5rem;
  }
  #mattails-cta-countdown .mattails-countdown-timer {
    display: flex;
    flex-wrap: wrap; /* Dodane: pozwala na zawijanie elementów na mniejszych ekranach */
    justify-content: center;
    gap: 0.75rem; /* Zmniejszony odstęp na małych ekranach */
    margin-bottom: 2.5rem;
  }
  #mattails-cta-countdown .mattails-timer-unit {
    display: flex;
    flex-direction: column;
    align-items: center;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    padding: 0.75rem; /* Zmniejszony padding na małych ekranach */
    border-radius: 0.5rem;
    min-width: 3.5rem; /* Zmniejszona minimalna szerokość */
  }
  #mattails-cta-countdown .mattails-unit-value {
    font-size: 1.5rem; /* Zmniejszony rozmiar czcionki */
    font-weight: 700;
    line-height: 1;
    margin-bottom: 0.25rem; /* Mniejszy margines */
  }
  #mattails-cta-countdown .mattails-unit-label {
    font-size: 0.65rem; /* Mniejszy rozmiar czcionki */
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: rgba(255, 255, 255, 0.7);
  }
  #mattails-cta-countdown .mattails-timer-separator {
    display: flex;
    align-items: center;
    font-size: 1.5rem; /* Zmniejszony rozmiar czcionki */
    font-weight: 700;
    color: rgba(255, 255, 255, 0.5);
  }
  /* Ukrycie sekcji sekund na najmniejszych ekranach */
  @media only screen and (max-width: 24rem) { /* 384px */
    #mattails-cta-countdown .mattails-timer-unit:last-of-type,
    #mattails-cta-countdown .mattails-timer-separator:last-of-type {
      display: none;
    }
  }
  #mattails-cta-countdown .mattails-cta-action {
    display: flex;
    flex-direction: column;
    align-items: center;
