// Countdown timer functionality
document.addEventListener('DOMContentLoaded', function() {
  // Set the date we're counting down to (1 month from current date for demo purposes)
  const currentDate = new Date();
  const futureDate = new Date(currentDate);
  futureDate.setDate(currentDate.getDate() + 30);

  const countDownDate = futureDate.getTime();

  // Funkcja sprawdzająca czy sekcja sekund jest widoczna (dla responsywności)
  function areSecondsVisible() {
    const secondsUnit = document.getElementById('mattails-seconds');
    return window.getComputedStyle(secondsUnit.parentElement).display !== 'none';
  }

  // Update the countdown every 1 second
  const countdownTimer = setInterval(function() {
    // Get today's date and time
    const now = new Date().getTime();

    // Find the distance between now and the countdown date
    const distance = countDownDate - now;

    // Time calculations for days, hours, minutes and seconds
    const days = Math.floor(distance / (1000 * 60 * 60 * 24));
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    // Display the result
    document.getElementById('mattails-days').innerText = days.toString().padStart(2, '0');
    document.getElementById('mattails-hours').innerText = hours.toString().padStart(2, '0');
    document.getElementById('mattails-minutes').innerText = minutes.toString().padStart(2, '0');

    // Aktualizuj sekundy tylko jeśli są widoczne (oszczędność zasobów)
    if (areSecondsVisible()) {
      document.getElementById('mattails-seconds').innerText = seconds.toString().padStart(2, '0');
    }

    // If the countdown is finished, clear the interval and show expiration message
    if (distance < 0) {
      clearInterval(countdownTimer);
      document.getElementById('mattails-days').innerText = '00';
      document.getElementById('mattails-hours').innerText = '00';
      document.getElementById('mattails-minutes').innerText = '00';
      document.getElementById('mattails-seconds').innerText = '00';

      // Optionally change the button text or disable it
      const ctaButton = document.querySelector('.mattails-cta-button');
      if (ctaButton) {
        ctaButton.innerText = 'Oferta wygasła';
        ctaButton.style.background = '#64748b';
        ctaButton.style.cursor = 'not-allowed';
        ctaButton.style.pointerEvents = 'none';
      }

      const ctaNote = document.querySelector('.mattails-cta-note');
      if (ctaNote) {
        ctaNote.innerText = 'Nasza specjalna oferta zakończyła się. Sprawdź później, aby znaleźć więcej promocji.';
      }
    }
  }, 1000);

  // Dodaj obsługę zmiany rozmiaru okna, aby dostosować wyświetlanie
  window.addEventListener('resize', function() {
    const secondsUnit = document.getElementById('mattails-seconds');
    if (areSecondsVisible()) {
      // Aktualizuj sekundy jeśli są widoczne
      const now = new Date().getTime();
      const distance = countDownDate - now;
      const seconds = Math.floor((distance % (1000 * 60)) / 1000);
      secondsUnit.innerText = seconds.toString().padStart(2, '0');
    }
  });
});

