css:
/*-- -------------------------- -->
<---      Modern Gradient Hero   -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-hero-gradient {
    padding: clamp(8rem, 25vw, 12rem) 1rem clamp(3rem, 15vw, 6rem);
    position: relative;
    z-index: 1;
    overflow: hidden;
  }
  #mattails-hero-gradient .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 3rem;
  }
  #mattails-hero-gradient .mattails-content {
    text-align: center;
    width: 100%;
    max-width: 40rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
    z-index: 10;
  }
  #mattails-hero-gradient .mattails-topper {
    font-size: clamp(0.875rem, 1.5vw, 1rem);
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: #8b5cf6;
    margin-bottom: 0.5rem;
  }
  #mattails-hero-gradient .mattails-title {
    font-size: clamp(2rem, 5vw, 3.5rem);
    font-weight: 800;
    line-height: 1.2;
    margin: 0 0 1rem;
    color: #fff;
  }
  #mattails-hero-gradient .mattails-text {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.5;
    max-width: 35rem;
    margin: 0 0 2rem;
    color: rgba(255, 255, 255, 0.9);
  }
  #mattails-hero-gradient .mattails-button-group {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    width: 100%;
    max-width: 25rem;
  }
  #mattails-hero-gradient .mattails-button-solid {
    font-size: 1rem;
    font-weight: 700;
    text-decoration: none;
    text-align: center;
    padding: 1rem 2rem;
    border-radius: 0.25rem;
    background: linear-gradient(to right, #8b5cf6, #6366f1);
    color: #fff;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    transition: transform 0.3s, box-shadow 0.3s;
    position: relative;
    overflow: hidden;
    width: 100%;
  }
  #mattails-hero-gradient .mattails-button-solid:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }
  #mattails-hero-gradient .mattails-button-transparent {
    font-size: 1rem;
    font-weight: 700;
    text-decoration: none;
    text-align: center;
    padding: 0.9rem 2rem;
    border-radius: 0.25rem;
    border: 2px solid rgba(255, 255, 255, 0.3);
    color: #fff;
    background: transparent;
    width: 100%;
    display: inline-block;
    transition: background-color 0.3s, border-color 0.3s;
  }
  #mattails-hero-gradient .mattails-button-transparent:hover {
    background-color: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.5);
  }
  #mattails-hero-gradient .mattails-graphic {
    width: 100%;
    max-width: 30rem;
    height: 20rem;
    position: relative;
  }
  #mattails-hero-gradient .mattails-floating-card {
    padding: 1rem;
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    gap: 0.75rem;
    position: absolute;
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.15);
  }
  #mattails-hero-gradient .mattails-card1 {
    top: 2rem;
    left: 1rem;
    animation: float 6s ease-in-out infinite;
  }
  #mattails-hero-gradient .mattails-card2 {
    bottom: 3rem;
    right: 1rem;
    animation: float 8s ease-in-out infinite 1s;
  }
  #mattails-hero-gradient .mattails-card-icon {
    width: 2.5rem;
    height: 2.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background: #8b5cf6;
    border-radius: 50%;
  }
  #mattails-hero-gradient .mattails-card-icon img {
    width: 1.25rem;
    height: auto;
    filter: brightness(0) invert(1);
  }
  #mattails-hero-gradient .mattails-card-text {
    font-size: 0.875rem;
    font-weight: 700;
    margin: 0;
    color: #1f2937;
  }
  #mattails-hero-gradient .mattails-sphere {
    width: 15rem;
    height: 15rem;
    background: linear-gradient(135deg, #8b5cf6 0%, #3b82f6 100%);
    border-radius: 50%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 0.8;
    filter: blur(1px);
    animation: pulse 5s ease-in-out infinite;
  }
  #mattails-hero-gradient .mattails-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
    background: linear-gradient(to bottom right, #1e1b4b, #1e293b);
  }
  #mattails-hero-gradient .mattails-gradient-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 20% 50%, rgba(139, 92, 246, 0.15) 0%, transparent 50%);
  }

  @keyframes float {
    0% { transform: translateY(0); }
    50% { transform: translateY(-15px); }
    100% { transform: translateY(0); }
  }

  @keyframes pulse {
    0% { transform: translate(-50%, -50%) scale(1); opacity: 0.8; }
    50% { transform: translate(-50%, -50%) scale(1.05); opacity: 0.9; }
    100% { transform: translate(-50%, -50%) scale(1); opacity: 0.8; }
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-hero-gradient .mattails-container {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }
  #mattails-hero-gradient .mattails-content {
    text-align: left;
    align-items: flex-start;
    width: 50%;
  }
  #mattails-hero-gradient .mattails-button-group {
    flex-direction: row;
  }
  #mattails-hero-gradient .mattails-graphic {
    width: 45%;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-hero-gradient .mattails-graphic {
    height: 30rem;
  }
  #mattails-hero-gradient .mattails-card1 {
    top: 6rem;
    left: 2rem;
  }
  #mattails-hero-gradient .mattails-card2 {
    bottom: 8rem;
    right: 2rem;
  }
  #mattails-hero-gradient .mattails-sphere {
    width: 20rem;
    height: 20rem;
  }
}

/*-- -------------------------- -->
<---        Split Image Hero     -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-hero-split {
    padding: 6rem 1rem 3rem;
    position: relative;
    overflow: hidden;
    background-color: #f8fafc;
  }
  #mattails-hero-split .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    display: flex;
    flex-direction: column;
    gap: 3rem;
  }
  #mattails-hero-split .mattails-content-side {
    max-width: 35rem;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
  }
  #mattails-hero-split .mattails-overline {
    font-size: 0.875rem;
    font-weight: 700;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    color: #0ea5e9;
    margin-bottom: 1rem;
  }
  #mattails-hero-split .mattails-headline {
    font-size: clamp(2rem, 5vw, 3.25rem);
    font-weight: 800;
    line-height: 1.2;
    color: #0f172a;
    margin: 0 0 1.5rem;
  }
  #mattails-hero-split .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #475569;
    margin: 0 0 2rem;
  }
  #mattails-hero-split .mattails-cta-container {
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 2rem;
  }
  #mattails-hero-split .mattails-cta-button {
    display: inline-block;
    font-size: 1rem;
    font-weight: 600;
    text-decoration: none;
    text-align: center;
    padding: 1rem 2rem;
    background-color: #0ea5e9;
    color: white;
    border-radius: 0.25rem;
    transition: background-color 0.3s, transform 0.3s;
  }
  #mattails-hero-split .mattails-cta-button:hover {
    background-color: #0284c7;
    transform: translateY(-2px);
  }
  #mattails-hero-split .mattails-stats {
    display: flex;
    align-items: center;
    gap: 1.5rem;
  }
  #mattails-hero-split .mattails-stat {
    display: flex;
    flex-direction: column;
  }
  #mattails-hero-split .mattails-stat-number {
    font-size: 1.875rem;
    font-weight: 800;
    color: #0f172a;
    line-height: 1;
  }
  #mattails-hero-split .mattails-stat-label {
    font-size: 0.875rem;
    color: #64748b;
    margin-top: 0.25rem;
  }
  #mattails-hero-split .mattails-stat-divider {
    width: 1px;
    height: 2.5rem;
    background-color: #cbd5e1;
  }
  #mattails-hero-split .mattails-image-side {
    position: relative;
    width: 100%;
    height: 25rem;
  }
  #mattails-hero-split .mattails-image-container {
    position: relative;
    width: 100%;
    height: 100%;
    z-index: 2;
  }
  #mattails-hero-split .mattails-hero-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 0.5rem;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
  #mattails-hero-split .mattails-image-accent {
    position: absolute;
    width: 70%;
    height: 70%;
    background-color: #bae6fd;
    border-radius: 0.5rem;
    bottom: -1rem;
    right: -1rem;
    z-index: 1;
  }
  #mattails-hero-split .mattails-floating-badge {
    position: absolute;
    bottom: -1.5rem;
    left: 2rem;
    background: white;
    border-radius: 2rem;
    padding: 0.75rem 1.25rem;
    display: flex;
    align-items: center;
    gap: 0.75rem;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }
  #mattails-hero-split .mattails-badge-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 2rem;
    height: 2rem;
    background-color: #0ea5e9;
    border-radius: 50%;
    color: white;
  }
  #mattails-hero-split .mattails-badge-text {
    font-size: 0.875rem;
    font-weight: 600;
    color: #0f172a;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-hero-split {
    padding: 8rem 1rem 8rem;
  }
  #mattails-hero-split .mattails-container {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    gap: 4rem;
  }
  #mattails-hero-split .mattails-content-side {
    width: 50%;
  }
  #mattails-hero-split .mattails-cta-container {
    flex-direction: row;
    align-items: center;
    gap: 3rem;
  }
  #mattails-hero-split .mattails-image-side {
    width: 45%;
    height: 30rem;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-hero-split {
    padding: 10rem 1rem 10rem;
  }
  #mattails-hero-split .mattails-content-side {
    width: 45%;
  }
  #mattails-hero-split .mattails-image-side {
    width: 50%;
    height: 35rem;
  }
  #mattails-hero-split .mattails-image-accent {
    width: 50%;
    height: 80%;
    bottom: -2rem;
    right: -2rem;
  }
}

/*-- -------------------------- -->
<---        3D Shapes Hero       -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-hero-3d {
    padding: 6rem 1rem 4rem;
    position: relative;
    overflow: hidden;
    background: radial-gradient(circle at 80% 10%, #9333ea 0%, #4338ca 50%, #1e3a8a 100%);
    min-height: 85vh;
  }
  #mattails-hero-3d .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 4rem;
  }
  #mattails-hero-3d .mattails-content {
    text-align: center;
    max-width: 40rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    z-index: 10;
  }
  #mattails-hero-3d .mattails-badge {
    display: inline-block;
    background: rgba(255, 255, 255, 0.15);
    backdrop-filter: blur(8px);
    padding: 0.5rem 1rem;
    border-radius: 2rem;
    font-size: 0.875rem;
    font-weight: 600;
    color: #fff;
    margin-bottom: 1.5rem;
  }
  #mattails-hero-3d .mattails-title {
    font-size: clamp(2.25rem, 5vw, 4rem);
    font-weight: 800;
    line-height: 1.1;
    background: linear-gradient(120deg, #fff, #bfdbfe);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0 0 1.5rem;
  }
  #mattails-hero-3d .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.8);
    margin: 0 0 2rem;
    max-width: 35rem;
  }
  #mattails-hero-3d .mattails-cta-group {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    width: 100%;
    max-width: 25rem;
    margin-bottom: 3rem;
  }
  #mattails-hero-3d .mattails-primary-button {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 1rem 2rem;
    background: linear-gradient(90deg, #3b82f6, #8b5cf6);
    color: white;
    font-weight: 600;
    border-radius: 0.25rem;
    text-decoration: none;
    transition: transform 0.3s, box-shadow 0.3s;
    width: 100%;
  }
  #mattails-hero-3d .mattails-primary-button:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 25px -5px rgba(0, 0, 0, 0.3);
  }
  #mattails-hero-3d .mattails-secondary-button {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 1rem 2rem;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(8px);
    color: white;
    font-weight: 600;
    border-radius: 0.25rem;
    text-decoration: none;
    transition: background 0.3s;
    width: 100%;
  }
  #mattails-hero-3d .mattails-secondary-button:hover {
    background: rgba(255, 255, 255, 0.2);
  }
  #mattails-hero-3d .mattails-trust-bar {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    width: 100%;
  }
  #mattails-hero-3d .mattails-trust-text {
    font-size: 0.875rem;
    color: rgba(255, 255, 255, 0.7);
    margin: 0;
  }
  #mattails-hero-3d .mattails-logos {
    display: flex;
    gap: 2rem;
    align-items: center;
    flex-wrap: wrap;
    justify-content: center;
  }
  #mattails-hero-3d .mattails-logo {
    font-size: 1rem;
    font-weight: 700;
    color: rgba(255, 255, 255, 0.5);
    letter-spacing: 0.05em;
  }
  #mattails-hero-3d .mattails-visual {
    position: relative;
    width: 100%;
    height: 20rem;
    perspective: 1000px;
  }
  #mattails-hero-3d .mattails-shape {
    position: absolute;
    transform-style: preserve-3d;
    backface-visibility: hidden;
  }
  #mattails-hero-3d .mattails-cube {
    width: 5rem;
    height: 5rem;
    background: linear-gradient(45deg, #3b82f6, #60a5fa);
    border-radius: 1rem;
    top: 20%;
    left: 20%;
    animation: float 6s ease-in-out infinite, rotate 12s linear infinite;
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
  }
  #mattails-hero-3d .mattails-sphere {
    width: 4rem;
    height: 4rem;
    background: linear-gradient(45deg, #ec4899, #f472b6);
    border-radius: 50%;
    top: 60%;
    left: 10%;
    animation: float 8s ease-in-out infinite 1s, rotate 10s linear infinite reverse;
    box-shadow: 0 0 20px rgba(236, 72, 153, 0.5);
  }
  #mattails-hero-3d .mattails-cylinder {
    width: 3rem;
    height: 6rem;
    background: linear-gradient(45deg, #10b981, #34d399);
    border-radius: 3rem;
    top: 50%;
    right: 15%;
    animation: float 7s ease-in-out infinite 0.5s, rotate 14s linear infinite;
    box-shadow: 0 0 20px rgba(16, 185, 129, 0.5);
  }
  #mattails-hero-3d .mattails-torus {
    width: 4rem;
    height: 4rem;
    border: 0.75rem solid #f59e0b;
    border-radius: 50%;
    top: 20%;
    right: 20%;
    animation: float 5s ease-in-out infinite 1.5s, rotate 8s linear infinite;
    box-shadow: 0 0 20px rgba(245, 158, 11, 0.5);
  }
  #mattails-hero-3d .mattails-device-mockup {
    position: absolute;
    width: 80%;
    max-width: 20rem;
    height: 12rem;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border-radius: 0.5rem;
    overflow: hidden;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    box-shadow: 0 20px 50px rgba(0, 0, 0, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }
  #mattails-hero-3d .mattails-screen {
    width: 100%;
    height: 100%;
    padding: 1rem;
  }
  #mattails-hero-3d .mattails-screen-content {
    height: 100%;
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
  }
  #mattails-hero-3d .mattails-screen-element {
    background: rgba(255, 255, 255, 0.15);
    border-radius: 0.25rem;
  }
  #mattails-hero-3d .mattails-screen-header {
    height: 20%;
  }
  #mattails-hero-3d .mattails-screen-body {
    height: 60%;
  }
  #mattails-hero-3d .mattails-screen-footer {
    height: 20%;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-20px); }
  }

  @keyframes rotate {
    from { transform: rotateY(0) rotateX(0); }
    to { transform: rotateY(360deg) rotateX(360deg); }
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-hero-3d .mattails-container {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
  }
  #mattails-hero-3d .mattails-content {
    width: 55%;
    text-align: left;
    align-items: flex-start;
  }
  #mattails-hero-3d .mattails-cta-group {
    flex-direction: row;
  }
  #mattails-hero-3d .mattails-visual {
    width: 40%;
    height: 25rem;
  }
  #mattails-hero-3d .mattails-trust-bar {
    flex-direction: row;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-hero-3d {
    padding: 8rem 1rem;
  }
  #mattails-hero-3d .mattails-content {
    width: 45%;
  }
  #mattails-hero-3d .mattails-visual {
    width: 50%;
    height: 30rem;
  }
  #mattails-hero-3d .mattails-device-mockup {
    max-width: 25rem;
    height: 14rem;
  }
  #mattails-hero-3d .mattails-cube {
    width: 6rem;
    height: 6rem;
  }
  #mattails-hero-3d .mattails-sphere {
    width: 5rem;
    height: 5rem;
  }
  #mattails-hero-3d .mattails-cylinder {
    width: 4rem;
    height: 7rem;
  }
  #mattails-hero-3d .mattails-torus {
    width: 5rem;
    height: 5rem;
  }
}

/*-- -------------------------- -->
<---     Video Background Hero   -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-hero-video {
    position: relative;
    overflow: hidden;
    height: 100vh;
    display: flex;
    align-items: center;
    width: 100%;
  }
  #mattails-hero-video .mattails-video-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    overflow: hidden;
  }
  #mattails-hero-video .mattails-background-video {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    min-width: 100%;
    min-height: 100%;
    width: auto;
    height: auto;
    object-fit: cover;
  }
  #mattails-hero-video .mattails-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to right, rgba(0, 0, 0, 0.8) 0%, rgba(0, 0, 0, 0.6) 100%);
    z-index: 2;
  }
  #mattails-hero-video .mattails-container {
    position: relative;
    z-index: 3;
    width: 100%;
    max-width: 80rem;
    margin: 0 auto;
    padding: 0 1rem;
  }
  #mattails-hero-video .mattails-hero-content {
    max-width: 40rem;
    color: white;
  }
  #mattails-hero-video .mattails-tag-line {
    display: flex;
    align-items: center;
