// This code would create more dynamic wave animations if implemented
document.addEventListener('DOMContentLoaded', function() {
  // Parallax effect for waves on mouse movement
  const waveContainer = document.querySelector('.mattails-wave-container');
  const waves = document.querySelectorAll('.mattails-wave');

  document.addEventListener('mousemove', function(e) {
    const x = e.clientX / window.innerWidth;
    const y = e.clientY / window.innerHeight;

    waves.forEach((wave, index) => {
      const speed = (index + 1) * 2;
      const offsetX = (x - 0.5) * speed;
      const offsetY = (y - 0.5) * speed / 2;

      wave.style.transform = `translateX(${offsetX}px) translateY(${offsetY}px)`;
    });
  });

  // Additional animation for floating cards
  const cards = document.querySelectorAll('.mattails-floating-card');

  document.addEventListener('scroll', function() {
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

    cards.forEach((card, index) => {
      const speed = (index + 1) * 0.2;
      card.style.transform = `translateY(${scrollTop * speed}px)`;
    });
  });
});
