/*-- -------------------------- -->
<---     Simple Services Grid     -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-services-grid {
    padding: 4rem 1rem;
    background-color: #f8fafc;
  }
  #mattails-services-grid .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
  }
  #mattails-services-grid .mattails-header {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto 3rem;
  }
  #mattails-services-grid .mattails-overline {
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: #3b82f6;
    display: block;
    margin-bottom: 0.5rem;
  }
  #mattails-services-grid .mattails-title {
    font-size: clamp(1.875rem, 3vw, 2.5rem);
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-services-grid .mattails-subtitle {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #64748b;
    margin: 0;
  }
  #mattails-services-grid .mattails-services {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
  }
  #mattails-services-grid .mattails-service-card {
    background-color: white;
    border-radius: 0.5rem;
    padding: 2rem;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s, box-shadow 0.3s;
  }
  #mattails-services-grid .mattails-service-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
  #mattails-services-grid .mattails-icon-wrapper {
    width: 3rem;
    height: 3rem;
    background-color: #eff6ff;
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    color: #3b82f6;
  }
  #mattails-services-grid .mattails-service-title {
    font-size: 1.25rem;
    font-weight: 600;
    color: #0f172a;
    margin: 0 0 0.75rem;
  }
  #mattails-services-grid .mattails-service-description {
    font-size: 1rem;
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 1.5rem;
  }
  #mattails-services-grid .mattails-service-link {
    font-size: 0.875rem;
    font-weight: 600;
    color: #3b82f6;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    transition: color 0.3s;
  }
  #mattails-services-grid .mattails-service-link:hover {
    color: #1d4ed8;
  }
  #mattails-services-grid .mattails-service-link::after {
    content: '→';
    display: inline-block;
    transition: transform 0.3s;
  }
  #mattails-services-grid .mattails-service-link:hover::after {
    transform: translateX(4px);
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-services-grid {
    padding: 5rem 1rem;
  }
  #mattails-services-grid .mattails-services {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-services-grid {
    padding: 6rem 1rem;
  }
  #mattails-services-grid .mattails-services {
    grid-template-columns: repeat(4, 1fr);
  }
}

/*-- -------------------------- -->
<---   Services With Hover Effect  -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-services-hover {
    padding: 4rem 1rem;
    background-color: #f8fafc;
