  #mattails-services-hover .mattails-service-content {
    padding: 2rem;
    position: relative;
    z-index: 2;
  }
  #mattails-services-hover .mattails-icon-wrapper {
    width: 3.5rem;
    height: 3.5rem;
    background-color: #eff6ff;
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    color: #3b82f6;
    transition: background-color 0.3s, color 0.3s;
  }
  #mattails-services-hover .mattails-service-item:hover .mattails-icon-wrapper {
    background-color: #3b82f6;
    color: white;
  }
  #mattails-services-hover .mattails-service-title {
    font-size: 1.25rem;
    font-weight: 600;
    color: #0f172a;
    margin: 0 0 0.75rem;
    transition: color 0.3s;
  }
  #mattails-services-hover .mattails-service-item:hover .mattails-service-title {
    color: #3b82f6;
  }
  #mattails-services-hover .mattails-service-description {
    font-size: 1rem;
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 1.5rem;
  }
  #mattails-services-hover .mattails-learn-more {
    font-size: 0.875rem;
    font-weight: 600;
    color: #3b82f6;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    transition: color 0.3s;
  }
  #mattails-services-hover .mattails-learn-more:hover {
    color: #1d4ed8;
  }
  #mattails-services-hover .mattails-learn-more::after {
    content: '→';
    display: inline-block;
    transition: transform 0.3s;
  }
  #mattails-services-hover .mattails-learn-more:hover::after {
    transform: translateX(4px);
  }
  #mattails-services-hover .mattails-service-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.05) 0%, rgba(59, 130, 246, 0) 100%);
    z-index: 1;
    opacity: 0;
    transition: opacity 0.3s;
  }
  #mattails-services-hover .mattails-service-item:hover .mattails-service-overlay {
    opacity: 1;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-services-hover {
    padding: 5rem 1rem;
  }
  #mattails-services-hover .mattails-services-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-services-hover {
    padding: 6rem 1rem;
  }
  #mattails-services-hover .mattails-services-grid {
    grid-template-columns: repeat(3, 1fr);
  }
}

/*-- -------------------------- -->
<---   Services With Cards and BG  -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-services-cards-bg {
    padding: 4rem 1rem;
    background-color: #f8fafc;
    position: relative;
    overflow: hidden;
  }
  #mattails-services-cards-bg .mattails-bg-decoration {
    position: absolute;
    top: -10%;
    right: -30%;
    width: 100%;
    height: 50%;
    background: linear-gradient(135deg, #dbeafe 0%, #eff6ff 100%);
    border-radius: 50%;
    transform: rotate(-15deg);
    opacity: 0.5;
    z-index: 1;
  }
  #mattails-services-cards-bg .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    position: relative;
    z-index: 2;
  }
  #mattails-services-cards-bg .mattails-header {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto 3rem;
  }
  #mattails-services-cards-bg .mattails-subtitle {
    display: inline-block;
    font-size: 0.875rem;
    font-weight: 600;
    color: #3b82f6;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    margin-bottom: 0.75rem;
  }
  #mattails-services-cards-bg .mattails-title {
    font-size: clamp(1.875rem, 3vw, 2.5rem);
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-services-cards-bg .mattails-description {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #64748b;
    margin: 0;
  }
  #mattails-services-cards-bg .mattails-services-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
  }
  #mattails-services-cards-bg .mattails-service-card {
    background-color: white;
    border-radius: 1rem;
    padding: 2rem;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.05), 0 4px 6px -2px rgba(0, 0, 0, 0.01);
    border: 1px solid #f1f5f9;
    transition: transform 0.3s, box-shadow 0.3s;
  }
  #mattails-services-cards-bg .mattails-service-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
  #mattails-services-cards-bg .mattails-icon-box {
    width: 4rem;
    height: 4rem;
    background-color: #eff6ff;
    border-radius: 1rem;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    color: #3b82f6;
    transition: background-color 0.3s, color 0.3s;
  }
  #mattails-services-cards-bg .mattails-service-card:hover .mattails-icon-box {
    background-color: #3b82f6;
    color: white;
  }
  #mattails-services-cards-bg .mattails-card-title {
    font-size: 1.25rem;
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-services-cards-bg .mattails-card-description {
    font-size: 1rem;
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 1.5rem;
  }
  #mattails-services-cards-bg .mattails-features-list {
    list-style: none;
    padding: 0;
    margin: 0 0 1.5rem;
  }
  #mattails-services-cards-bg .mattails-features-list li {
    display: flex;
    align-items: center;
    font-size: 0.875rem;
    color: #475569;
    margin-bottom: 0.5rem;
  }
  #mattails-services-cards-bg .mattails-features-list li::before {
    content: '✓';
    display: inline-block;
    width: 1rem;
    margin-right: 0.5rem;
    color: #3b82f6;
    font-weight: bold;
  }
  #mattails-services-cards-bg .mattails-card-link {
    display: inline-block;
    font-size: 0.875rem;
    font-weight: 600;
    color: #3b82f6;
    text-decoration: none;
    transition: color 0.3s;
  }
  #mattails-services-cards-bg .mattails-card-link:hover {
    color: #1d4ed8;
  }
  #mattails-services-cards-bg .mattails-card-link::after {
    content: ' →';
    display: inline-block;
    transition: transform 0.3s;
  }
  #mattails-services-cards-bg .mattails-card-link:hover::after {
    transform: translateX(4px);
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-services-cards-bg {
    padding: 5rem 1rem;
  }
  #mattails-services-cards-bg .mattails-services-grid {
    grid-template-columns: repeat(2, 1fr);
  }
  #mattails-services-cards-bg .mattails-bg-decoration {
    right: -20%;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-services-cards-bg {
    padding: 6rem 1rem;
  }
  #mattails-services-cards-bg .mattails-services-grid {
    grid-template-columns: repeat(3, 1fr);
  }
  #mattails-services-cards-bg .mattails-bg-decoration {
    right: 0;
    width: 70%;
  }
}

/*-- -------------------------- -->
<---      Services Timeline       -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-services-timeline {
    padding: 4rem 1rem;
    background-color: #f8fafc;
  }
  #mattails-services-timeline .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
  }
  #mattails-services-timeline .mattails-header {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto 4rem;
  }
  #mattails-services-timeline .mattails-overline {
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: #4f46e5;
    display: block;
    margin-bottom: 0.5rem;
  }
  #mattails-services-timeline .mattails-title {
    font-size: clamp(1.875rem, 3vw, 2.5rem);
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 1rem;
  }
  #mattails-services-timeline .mattails-subtitle {
    font-size: clamp(1rem, 2vw, 1.125rem);
    line-height: 1.6;
    color: #64748b;
    margin: 0;
  }
  #mattails-services-timeline .mattails-timeline {
    position: relative;
    max-width: 60rem;
    margin: 0 auto;
  }
  #mattails-services-timeline .mattails-timeline-line {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 2.25rem;
    width: 2px;
    background: linear-gradient(to bottom, #e0e7ff, #c7d2fe, #a5b4fc, #818cf8, #4f46e5);
    transform: translateX(-50%);
  }
  #mattails-services-timeline .mattails-service-step {
    position: relative;
    padding-left: 4.5rem;
    margin-bottom: 4rem;
  }
  #mattails-services-timeline .mattails-service-step:last-child {
    margin-bottom: 0;
  }
  #mattails-services-timeline .mattails-step-marker {
    position: absolute;
    top: 0;
    left: 2.25rem;
    width: 3.5rem;
    height: 3.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: white;
    border: 3px solid #4f46e5;
    border-radius: 50%;
    transform: translateX(-50%);
    z-index: 2;
  }
  #mattails-services-timeline .mattails-step-number {
    font-size: 1.125rem;
    font-weight: 700;
    color: #4f46e5;
  }
  #mattails-services-timeline .mattails-step-content {
    background-color: white;
    border-radius: 0.75rem;
    padding: 2rem;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.05), 0 4px 6px -2px rgba(0, 0, 0, 0.01);
    border: 1px solid #f1f5f9;
  }
  #mattails-services-timeline .mattails-icon-container {
    width: 3.5rem;
    height: 3.5rem;
    background-color: #e0e7ff;
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    color: #4f46e5;
  }
  #mattails-services-timeline .mattails-icon {
    stroke: currentColor;
    stroke-width: 2;
  }
  #mattails-services-timeline .mattails-step-title {
    font-size: 1.25rem;
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 0.75rem;
  }
  #mattails-services-timeline .mattails-step-description {
    font-size: 1rem;
    line-height: 1.6;
    color: #64748b;
    margin: 0 0 1.5rem;
  }
  #mattails-services-timeline .mattails-step-benefits {
    list-style: none;
    padding: 0;
    margin: 0;
  }
  #mattails-services-timeline .mattails-step-benefits li {
    display: flex;
    align-items: center;
    font-size: 0.875rem;
    color: #475569;
    margin-bottom: 0.5rem;
  }
  #mattails-services-timeline .mattails-step-benefits li::before {
    content: '✓';
    display: inline-block;
    font-size: 0.75rem;
    width: 1.25rem;
    height: 1.25rem;
    margin-right: 0.5rem;
    color: white;
    background-color: #4f46e5;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-services-timeline {
    padding: 5rem 1rem;
  }
  #mattails-services-timeline .mattails-timeline-line {
    left: 50%;
  }
  #mattails-services-timeline .mattails-service-step {
    padding-left: 0;
    width: 50%;
  }
  #mattails-services-timeline .mattails-service-step:nth-child(odd) {
    margin-left: 0;
    margin-right: auto;
    padding-right: 3rem;
  }
  #mattails-services-timeline .mattails-service-step:nth-child(even) {
    margin-left: auto;
    margin-right: 0;
    padding-left: 3rem;
  }
  #mattails-services-timeline .mattails-step-marker {
    left: 50%;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-services-timeline {
    padding: 6rem 1rem;
  }
  #mattails-services-timeline .mattails-service-step:nth-child(odd) {
    padding-right: 4rem;
  }
  #mattails-services-timeline .mattails-service-step:nth-child(even) {
    padding-left: 4rem;
  }
}

/*-- -------------------------- -->
<--- Services With Gradient BG -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-services-gradient {
    padding: 6rem 1rem;
    position: relative;
    overflow: hidden;
    background-color: #f8f9fc;
  }
  #mattails-services-gradient .mattails-bg-gradient {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(168, 85, 247, 0.1) 35%, rgba(236, 72, 153, 0.05) 100%);
    z-index: 1;
  }
  #mattails-services-gradient .mattails-container {
    width: 100%;
    max-width: 80rem;
    margin: auto;
    position: relative;
    z-index: 2;
  }
  #mattails-services-gradient .mattails-header {
    text-align: center;
    max-width: 50rem;
    margin: 0 auto 4rem;
  }
  #mattails-services-gradient .mattails-badge-wrapper {
    margin-bottom: 1.5rem;
  }
  #mattails-services-gradient .mattails-badge {
    display: inline-block;
    padding: 0.5rem 1.25rem;
    border-radius: 2rem;
    font-size: 0.875rem;
    font-weight: 600;
    color: #6366f1;
    background: linear-gradient(90deg, rgba(99, 102, 241, 0.1) 0%, rgba(99, 102, 241, 0.2) 100%);
    border: 1px solid rgba(99, 102, 241, 0.2);
  }
  #mattails-services-gradient .mattails-title {
    font-size: clamp(2rem, 4vw, 3rem);
    font-weight: 800;
    background: linear-gradient(90deg, #4f46e5, #7c3aed, #be185d);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0 0 1.25rem;
    line-height: 1.2;
  }
  #mattails-services-gradient .mattails-description {
    font-size: clamp(1rem, 2vw, 1.25rem);
    line-height: 1.6;
    color: #6b7280;
    margin: 0;
  }
  #mattails-services-gradient .mattails-services-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
  }
  #mattails-services-gradient .mattails-service-card {
    background-color: white;
    border-radius: 1rem;
    overflow: hidden;
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.05), 0 8px 10px -6px rgba(0, 0, 0, 0.01);
    transition: transform 0.3s, box-shadow 0.3s;
    display: flex;
    flex-direction: column;
    height: 100%;
    border: 1px solid rgba(226, 232, 240, 0.8);
  }
  #mattails-services-gradient .mattails-service-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.1);
  }
  #mattails-services-gradient .mattails-card-header {
    padding: 2rem 2rem 0;
    display: flex;
    align-items: center;
    gap: 1rem;
  }
  #mattails-services-gradient .mattails-icon-circle {
    width: 3.5rem;
    height: 3.5rem;
    border-radius: 50%;
    display: flex;
    align-items: center;
