document.addEventListener('DOMContentLoaded', function() {
  const container = document.getElementById('mattails-services-immersive');
  const orb = container.querySelector('.mattails-glow-orb');
  const shapes = container.querySelectorAll('.mattails-geometric-shape');
  const gridLines = container.querySelectorAll('.mattails-grid-line');

  // Mouse movement parallax
  container.addEventListener('mousemove', function(e) {
    const x = e.clientX / window.innerWidth;
    const y = e.clientY / window.innerHeight;

    // Move orb inversely to mouse movement
    if (orb) {
      orb.style.transform = `translate(${(x - 0.5) * -30}px, ${(y - 0.5) * -30}px)`;
    }

    // Move each shape with different intensity
    shapes.forEach((shape, index) => {
      const intensity = (index + 1) * 10;
      shape.style.transform = `translate(${(x - 0.5) * intensity}px, ${(y - 0.5) * intensity}px) rotate(${(x - 0.5) * 20}deg)`;
    });

    // Move grid lines for perspective effect
    gridLines.forEach((line, index) => {
      const depth = index % 3 === 0 ? 20 : 10;
      if (line.classList.contains('horizontal')) {
        line.style.transform = `translateZ(-100px) translateY(${(y - 0.5) * depth}px)`;
      } else {
        line.style.transform = `translateZ(-100px) translateX(${(x - 0.5) * depth}px)`;
      }
    });
  });

  // Optional: Add scroll event to parallax cards
  window.addEventListener('scroll', function() {
    const scrollY = window.scrollY;
    const cards = container.querySelectorAll('.mattails-service-card');

    cards.forEach((card, index) => {
      const speed = 0.1 + (index * 0.05);
      const yOffset = scrollY * speed;
      const rotation = scrollY * 0.02 * (index % 2 === 0 ? 1 : -1);

      card.style.transform = `translateY(${yOffset}px) rotate(${rotation}deg)`;
      card.style.opacity = 1 - (scrollY * 0.001);
    });
  });

  // Initialize hover effects for service cards
  const serviceCards = container.querySelectorAll('.mattails-service-card');
  serviceCards.forEach(card => {
    // Add subtle tilt effect on hover
    card.addEventListener('mouseenter', function(e) {
      const cardRect = card.getBoundingClientRect();
      const centerX = cardRect.left + cardRect.width / 2;
      const centerY = cardRect.top + cardRect.height / 2;
      const x = e.clientX - centerX;
      const y = e.clientY - centerY;

      // Apply a subtle tilt based on mouse position relative to card center
      card.querySelector('.mattails-card-inner').style.transition = 'transform 0.2s cubic-bezier(0.34, 1.56, 0.64, 1)';
      card.querySelector('.mattails-card-inner').style.transform = `perspective(1000px) rotateY(${x * 0.01}deg) rotateX(${-y * 0.01}deg)`;
    });

    // Reset tilt on mouse leave
    card.addEventListener('mouseleave', function() {
      card.querySelector('.mattails-card-inner').style.transition = 'transform 0.8s cubic-bezier(0.34, 1.56, 0.64, 1)';
      card.querySelector('.mattails-card-inner').style.transform = 'rotateY(0deg) rotateX(0deg)';
    });

    // Add ripple effect on click
    card.addEventListener('click', function(e) {
      const ripple = document.createElement('div');
      ripple.classList.add('mattails-ripple');
      const rect = card.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      ripple.style.top = `${y}px`;
      ripple.style.left = `${x}px`;

      card.appendChild(ripple);

      setTimeout(() => {
        ripple.remove();
      }, 800);
    });
  });

  // Initialize intersection observer for fade-in animation
  if ('IntersectionObserver' in window) {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('mattails-in-view');
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.2 });

    // Observe all cards and intro content
    serviceCards.forEach(card => observer.observe(card));
    observer.observe(container.querySelector('.mattails-intro-content'));
  }
});
