/*-- -------------------------- -->
<---    Modern Testimonials     -->
<--- -------------------------- -*/

/* Mobile - 360px */
@media only screen and (min-width: 0rem) {
  #mattails-testimonials {
    padding: 4rem 1rem;
    background: linear-gradient(to bottom, #f8fafc, #f1f5f9);
    overflow: hidden;
  }
  #mattails-testimonials .mattails-container {
    width: 100%;
    max-width: 75rem;
    margin: 0 auto;
  }
  #mattails-testimonials .mattails-heading {
    text-align: center;
    max-width: 40rem;
    margin: 0 auto 3rem;
  }
  #mattails-testimonials .mattails-subheading {
    display: inline-block;
    font-size: 0.875rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: #3b82f6;
    margin-bottom: 1rem;
    position: relative;
  }
  #mattails-testimonials .mattails-subheading:before,
  #mattails-testimonials .mattails-subheading:after {
    content: '';
    position: absolute;
    top: 50%;
    width: 1.5rem;
    height: 1px;
    background-color: #3b82f6;
  }
  #mattails-testimonials .mattails-subheading:before {
    left: -2rem;
  }
  #mattails-testimonials .mattails-subheading:after {
    right: -2rem;
  }
  #mattails-testimonials .mattails-title {
    font-size: clamp(1.75rem, 4vw, 2.5rem);
    font-weight: 800;
    color: #0f172a;
    margin: 0 0 1rem;
    line-height: 1.2;
  }
  #mattails-testimonials .mattails-description {
    font-size: 1rem;
    line-height: 1.6;
    color: #64748b;
    margin: 0;
  }
  #mattails-testimonials .mattails-testimonials-wrapper {
    position: relative;
    padding: 1rem 0 3rem;
  }
  #mattails-testimonials .mattails-testimonial-controls {
    display: flex;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 2rem;
  }
  #mattails-testimonials .mattails-control-btn {
    width: 3rem;
    height: 3rem;
    border-radius: 50%;
    background-color: white;
    border: 1px solid #e2e8f0;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #475569;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  #mattails-testimonials .mattails-control-btn:hover {
    background-color: #3b82f6;
    color: white;
    border-color: #3b82f6;
    transform: translateY(-2px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }
  #mattails-testimonials .mattails-testimonials-track {
    display: flex;
    transition: transform 0.5s ease;
    min-height: 20rem;
    position: relative;
  }
  #mattails-testimonials .mattails-testimonial-slide {
    flex: 0 0 100%;
    opacity: 0;
    visibility: hidden;
    transition: opacity 0.5s, visibility 0.5s;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
  }
  #mattails-testimonials .mattails-testimonial-slide.active {
    opacity: 1;
    visibility: visible;
  }
  #mattails-testimonials .mattails-testimonial-card {
    background: white;
    border-radius: 1rem;
    padding: 2rem;
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.05), 0 10px 10px -5px rgba(0, 0, 0, 0.02);
    height: 100%;
    display: flex;
    flex-direction: column;
    position: relative;
    transition: transform 0.3s, box-shadow 0.3s;
  }
  #mattails-testimonials .mattails-testimonial-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
  #mattails-testimonials .mattails-quote-icon {
    color: #dbeafe;
    position: absolute;
    top: 1.5rem;
    right: 1.5rem;
  }
  #mattails-testimonials .mattails-testimonial-content {
    flex: 1;
    margin-bottom: 2rem;
  }
  #mattails-testimonials .mattails-testimonial-text {
    font-size: 1.125rem;
    line-height: 1.6;
    color: #334155;
    margin: 0;
    position: relative;
    z-index: 1;
  }
  #mattails-testimonials .mattails-testimonial-author {
    display: flex;
    align-items: center;
    border-top: 1px solid #f1f5f9;
    padding-top: 1.5rem;
  }
  #mattails-testimonials .mattails-author-image {
    width: 3rem;
    height: 3rem;
    border-radius: 50%;
    overflow: hidden;
    margin-right: 1rem;
    flex-shrink: 0;
  }
  #mattails-testimonials .mattails-author-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
  #mattails-testimonials .mattails-author-info {
    flex: 1;
  }
  #mattails-testimonials .mattails-author-name {
    font-size: 1rem;
    font-weight: 700;
    color: #0f172a;
    margin: 0 0 0.25rem;
  }
  #mattails-testimonials .mattails-author-role {
    font-size: 0.875rem;
    color: #64748b;
    margin: 0;
  }
  #mattails-testimonials .mattails-company-logo {
    width: 2.5rem;
    height: 2.5rem;
    background: #f1f5f9;
    border-radius: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    color: #3b82f6;
    font-size: 0.875rem;
  }
  #mattails-testimonials .mattails-testimonial-dots {
    display: flex;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 2rem;
  }
  #mattails-testimonials .mattails-dot {
    width: 0.75rem;
    height: 0.75rem;
    border-radius: 50%;
    background-color: #e2e8f0;
    border: none;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  #mattails-testimonials .mattails-dot.active {
    background-color: #3b82f6;
    transform: scale(1.2);
  }
}

/* Tablet - 768px */
@media only screen and (min-width: 48rem) {
  #mattails-testimonials {
    padding: 6rem 1rem;
  }
  #mattails-testimonials .mattails-testimonial-controls {
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    transform: translateY(-50%);
    justify-content: space-between;
    padding: 0 1rem;
    z-index: 2;
    margin-bottom: 0;
  }
  #mattails-testimonials .mattails-testimonial-card {
    padding: 3rem;
  }
}

/* Desktop - 1024px */
@media only screen and (min-width: 64rem) {
  #mattails-testimonials {
    padding: 8rem 1rem;
  }
  #mattails-testimonials .mattails-testimonials-track {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 2rem;
    min-height: auto;
  }
  #mattails-testimonials .mattails-testimonial-slide {
    position: relative;
    opacity: 1;
    visibility: visible;
  }
  #mattails-testimonials .mattails-testimonial-controls,
  #mattails-testimonials .mattails-testimonial-dots {
    display: none;
  }
}

