document.addEventListener('DOMContentLoaded', function() {
  // Elements
  const slides = document.querySelectorAll('.mattails-testimonial-slide');
  const dots = document.querySelectorAll('.mattails-dot');
  const prevBtn = document.querySelector('.mattails-prev');
  const nextBtn = document.querySelector('.mattails-next');

  let currentSlide = 0;
  const slideCount = slides.length;

  // Skip slider functionality if we're on desktop (the CSS already handles showing all slides)
  const isDesktop = window.innerWidth >= 1024;
  if (isDesktop) return;

  // Initialize slider
  function showSlide(index) {
    // Hide all slides
    slides.forEach(slide => {
      slide.classList.remove('active');
    });

    // Remove active class from all dots
    dots.forEach(dot => {
      dot.classList.remove('active');
    });

    // Show current slide and activate current dot
    slides[index].classList.add('active');
    dots[index].classList.add('active');
  }

  // Next slide
  function nextSlide() {
    currentSlide = (currentSlide + 1) % slideCount;
    showSlide(currentSlide);
  }

  // Previous slide
  function prevSlide() {
    currentSlide = (currentSlide - 1 + slideCount) % slideCount;
    showSlide(currentSlide);
  }

  // Event listeners
  prevBtn.addEventListener('click', prevSlide);
  nextBtn.addEventListener('click', nextSlide);

  // Add click events to dots
  dots.forEach((dot, index) => {
    dot.addEventListener('click', () => {
      currentSlide = index;
      showSlide(currentSlide);
    });
  });

  // Auto slide (optional)
  let slideInterval = setInterval(nextSlide, 5000);

  // Pause auto slide on hover
  const testimonialWrapper = document.querySelector('.mattails-testimonials-wrapper');
  testimonialWrapper.addEventListener('mouseenter', () => {
    clearInterval(slideInterval);
  });

  testimonialWrapper.addEventListener('mouseleave', () => {
    slideInterval = setInterval(nextSlide, 5000);
  });

  // Initialize the first slide
  showSlide(currentSlide);
});

