// Minimal JavaScript for interaction
document.addEventListener('DOMContentLoaded', function() {
  // Add hover effect to testimonial items
  const testimonialItems = document.querySelectorAll('.mattails-testimonial-item');

  testimonialItems.forEach(item => {
    item.addEventListener('mouseenter', function() {
      // Optional: Add additional hover effects beyond CSS
      // For example, you could add a slight rotation
      //this.style.transform = 'translateY(-4px) rotate(1deg)';
    });

    item.addEventListener('mouseleave', function() {
      // Reset any additional effects when mouse leaves
      //this.style.transform = 'translateY(0) rotate(0)';
    });
  });

  // Filter testimonials by category (if you have category buttons)
  const categoryButtons = document.querySelectorAll('.mattails-category-btn');
  if (categoryButtons.length > 0) {
    categoryButtons.forEach(button => {
      button.addEventListener('click', function() {
        // Remove active class from all buttons
        categoryButtons.forEach(btn => btn.classList.remove('active'));

        // Add active class to current button
        this.classList.add('active');

        const category = this.dataset.category;

        testimonialItems.forEach(item => {
          if (category === 'all') {
            item.style.display = 'block';
          } else {
            const itemCategory = item.dataset.category;
            item.style.display = itemCategory === category ? 'block' : 'none';
          }
        });

        // Adjust layout if using masonry or similar layouts
        // layoutRefresh();
      });
    });
  }

  // Optional: Add animation when testimonials come into view
  const handleIntersection = (entries, observer) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        entry.target.classList.add('animate');
        // Stop observing once animation is triggered
        observer.unobserve(entry.target);
      }
    });
  };

  // Check if IntersectionObserver is supported
  if ('IntersectionObserver' in window) {
    const options = {
      root: null, // Use viewport as root
      rootMargin: '0px',
      threshold: 0.1 // Trigger when 10% of element is visible
    };

    const observer = new IntersectionObserver(handleIntersection, options);

    testimonialItems.forEach(item => {
      observer.observe(item);
    });
  } else {
    // Fallback for browsers that don't support IntersectionObserver
    testimonialItems.forEach(item => {
      item.classList.add('animate');
    });
  }

  // Read More / Read Less functionality for long testimonials
  const expandButtons = document.querySelectorAll('.mattails-expand-btn');
  if (expandButtons.length > 0) {
    expandButtons.forEach(button => {
      button.addEventListener('click', function() {
        const testimonialBody = this.closest('.mattails-testimonial-item').querySelector('.mattails-testimonial-body');

        if (testimonialBody.classList.contains('expanded')) {
          testimonialBody.classList.remove('expanded');
          this.textContent = 'Read More';
        } else {
          testimonialBody.classList.add('expanded');
          this.textContent = 'Read Less';
        }
      });
    });
  }

  // Optional: Randomize testimonial order on page load
  function shuffleTestimonials() {
    const grid = document.querySelector('.mattails-testimonials-grid');
    if (!grid) return;

    const featuredItems = document.querySelectorAll('.mattails-testimonial-item.featured');
    const regularItems = document.querySelectorAll('.mattails-testimonial-item:not(.featured)');

    // Convert NodeList to Array to use Array methods
    const regularItemsArray = Array.from(regularItems);

    // Fisher-Yates shuffle algorithm
    for (let i = regularItemsArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [regularItemsArray[i], regularItemsArray[j]] = [regularItemsArray[j], regularItemsArray[i]];
    }

    // Clear grid and append featured items first, then shuffled regular items
    grid.innerHTML = '';
    featuredItems.forEach(item => grid.appendChild(item));
    regularItemsArray.forEach(item => grid.appendChild(item));
  }

  // Uncomment the line below to enable random order on page load
  // shuffleTestimonials();

  // Optional: Enable testimonial search functionality
  const searchInput = document.querySelector('.mattails-testimonial-search');
  if (searchInput) {
    searchInput.addEventListener('input', function() {
      const searchTerm = this.value.toLowerCase();

      testimonialItems.forEach(item => {
        const text = item.querySelector('.mattails-testimonial-text').textContent.toLowerCase();
        const name = item.querySelector('.mattails-reviewer-name').textContent.toLowerCase();
        const position = item.querySelector('.mattails-reviewer-position').textContent.toLowerCase();

        // Search in testimonial text, reviewer name and position
        if (text.includes(searchTerm) || name.includes(searchTerm) || position.includes(searchTerm)) {
          item.style.display = 'block';
        } else {
          item.style.display = 'none';
        }
      });
    });
  }
});
